/**
 * Run all tests in sequence
 */
async function runTests() {
  console.log('üß™ STARTING IMAGE UPLOAD TESTS');
  console.log('============================');
  console.log(`Server URL: ${API_BASE_URL}`);
  console.log(`Access Code configured: ${ACCESS_CODE ? 'Yes' : 'No'}`);
  
  try {
    // Test 1: Login
    const loginSuccess = await testLogin();
    if (!loginSuccess) {
      console.error('‚ùå Login failed, aborting remaining tests');
      return;
    }
    
    // Test 2: Create Location
    const createSuccess = await testCreateLocation();
    if (!createSuccess) {
      console.error('‚ùå Create location failed, aborting remaining tests');
      return;
    }
    
    // Test 3: Upload Image
    const uploadSuccess = await testUploadBase64Image();
    if (!uploadSuccess) {
      console.error('‚ùå Image upload failed, some tests may be skipped');
    }
    
    // Test 4: Get Image
    await testGetImage();
    
    // Test 5: Get Thumbnail
    await testGetThumbnail();
    
    // Test 6: Invalid MIME Type
    await testInvalidMimeType();
    
    // Test 7: Mismatched Extension
    await testMismatchedExtension();
    
    // Test 8: Clean Up
    await testCleanUp();
    
    console.log('\nüèÅ ALL TESTS COMPLETED');
  } catch (error) {
    console.error('\nüí• UNHANDLED ERROR DURING TESTS:');
    console.error(error);
  }
}

// Check for ACCESS_CODE before running tests
if (!ACCESS_CODE) {
  console.error('‚ùå ACCESS_CODE environment variable is not set. Tests will likely fail.');
  console.error('Please set the ACCESS_CODE environment variable and try again.');
  process.exit(1);
}

// Run the tests
runTests();